"""
MetaTrader5 import compatibility module.
The package installs as 'metatrader5' but code imports 'MetaTrader5'.
This module provides the correct import alias.
"""
import sys
import importlib

# Import the actual metatrader5 module
_metatrader5 = importlib.import_module('metatrader5')

# Export everything from metatrader5
for attr in dir(_metatrader5):
    if not attr.startswith('_'):
        setattr(sys.modules[__name__], attr, getattr(_metatrader5, attr))

# Also make sure the module itself is accessible
__all__ = dir(_metatrader5)

